import React from "react";
import { NavLink, Outlet, useLocation } from "react-router-dom";
import css from './Layout.module.css';

const Layout = () => {
  const location = useLocation();

  const activeLinkStyle = {
    color: location.pathname === "/" ? "orange" : "gray",
    fontWeight: "bold",
  };

  return (
    <div>
      <nav className={css.headerNav}>
        <NavLink exact to="/" className={css.navLink} activeStyle={activeLinkStyle}>
          <span className={css.navText}>Home</span>
        </NavLink>
        <NavLink to="/movies" className={css.navLink} activeStyle={activeLinkStyle}>
          <span className={css.navText}>Movies</span>
        </NavLink>
      </nav>
      <Outlet />
    </div>
  );
}

export default Layout;
