import React, { useState, useEffect } from 'react';
import { useParams, Link, Outlet, useLocation } from 'react-router-dom';
import { getMovieById } from 'API/movieApi';
import css from './MovieCard.module.css';
import { CgArrowTopLeftO } from 'react-icons/cg';

const MovieCard = () => {
  const { movieId } = useParams();
  const [movie, setMovie] = useState(null);
  const location = useLocation();

  useEffect(() => {
    const fetchMovie = async () => {
      try {
        const response = await getMovieById(movieId);
        setMovie(response);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMovie();
  }, [movieId]);

  const handleGoBack = () => {
    const searchParams = new URLSearchParams(location.search);
    const from = searchParams.get('from');
    if (from) {
      window.location.href = from; // Redirect to the previous page
    } else {
      // Handle the case when 'from' parameter is not available
    }
  };

  return (
    <div>
      {movie && (
        <div>
          <div className={css.backLink} onClick={handleGoBack}>
            <div className={css.back}>
              <span className={css.icon}>
                <CgArrowTopLeftO />
              </span>
            </div>
          </div>
          <div className={css.cardWrap}>
            <h3 className={css.movieTitle}>{movie.title}</h3>
            {/* Rest of the code */}
          </div>

          <Link to={`/movies/${movieId}/cast`} className={css.adLink}>
            <span className={css.adLink}>Cast</span>
          </Link>

          <span className={css.adLink}>
            <Link to={`/movies/${movieId}/review`} className={css.adLink}>
              Review
            </Link>
          </span>

          <Outlet />
        </div>
      )}
    </div>
  );
};

export default MovieCard;
